// Card.js

import React from 'react';
import Draggable from './Draggable';

const Card = ({ text, position, onDrop }) => {
    return (
        <Draggable onDrop={onDrop}>
            <div
                style={{
                    position: 'absolute',
                    left: position.x + 'px',
                    top: position.y + 'px',
                    width: 100,
                    height: 70,
                    border: '1px solid black',
                    backgroundColor: 'lightgray',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    userSelect: 'none',
                }}
            >
                {text}
            </div>
        </Draggable>
    );
};

export default Card;
