import React, { useState, useEffect } from 'react';
import Draggable from './Draggable';
import './Card.css'; // Assurez-vous d'importer votre fichier CSS

const Card = ({ text, position }) => {
    const [animate, setAnimate] = useState(true);

    // Désactiver l'animation après la première exécution
    useEffect(() => {
        setAnimate(false);
    }, []);

    return (
        <Draggable>
            <div
                className={`card ${animate ? 'animate' : ''}`}
                style={{
                    position: 'absolute',
                    left: position.x + 'px',
                    top: position.y + 'px',
                    width: 100,
                    height: 70,
                    border: '1px solid black',
                    backgroundColor: 'lightgray',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                }}
            >
                {text}
            </div>
        </Draggable>
    );
};

export default Card;
