// Card.js

import React, { useRef, useEffect } from 'react';

const Card = ({ text, appState }) => {
    const cardRef = useRef();

    useEffect(() => {
        if (cardRef.current) {
            // Utilisez les propriétés de appState pour mettre en œuvre le glisser-déposer
            const deltaX = appState.mouseX - appState.lastMouseX;
            const deltaY = appState.mouseY - appState.lastMouseY;

            if (appState.skipMoves > 5) {
                // Votre logique pour déplacer les éléments
                // shiftSelectedElements(appState);
                // ...

                // Mettez à jour la position de la carte avec translate3d
                const transform = `translate3d(${deltaX}px, ${deltaY}px, 0)`;
                cardRef.current.style.transform = transform;
            }
        }
    }, [appState]);

    return (
        <div
            ref={cardRef}
            style={{
                width: 100,
                height: 70,
                border: '1px solid black',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                margin: '10px',
            }}
        >
            {text}
        </div>
    );
};

export default Card;
