// Category.js

import React, { useRef, useState } from 'react';
import Draggable from './Draggable';
import CardDropZone from './CardDropZone';


const Category = ({ position, text }) => {
    const categoryRef = useRef();
    const thisSize = ({ x: 110, y: 100 });

    const [isCardOver, setIsCardOver] = useState(false);

    const handleCardEnter = () => {
        setIsCardOver(true);
    };

    const handleCardLeave = () => {
        setIsCardOver(false);
    };

    const handleCardDrop = (card) => {
        console.log(`Card dropped onto Category: ${card.text}`);
    };

    return (
        <Draggable>
            <div
                ref={categoryRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: position.x + 'px',
                    top: position.y + 'px',

                    // to adapt in css file
                    border: '1px solid black',
                    backgroundColor: 'lightcyan',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                {text}

                <CardDropZone
                />
            </div>
        </Draggable>
    );
};

export default Category;
