// Card.js

import React, { useState } from 'react';

const Card = ({ text }) => {
    const [isDragging, setDragging] = useState(false);

    const handleDragStart = (e) => {
        setDragging(true);
        e.dataTransfer.setData('text/plain', ''); // Nécessaire pour activer le glisser-déposer
    };

    const handleDragEnd = () => {
        setDragging(false);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleDrop = () => {
        // Logique de déplacement ici
        setDragging(false);
    };

    return (
        <div
            style={{
                width: 100,
                height: 70,
                border: '1px solid black',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                margin: '10px',
                cursor: 'grab',
                userSelect: 'none',
                transform: isDragging ? 'scale(1.1)' : 'scale(1)',
            }}
            draggable
            onDragStart={handleDragStart}
            onDragEnd={handleDragEnd}
            onDragOver={handleDragOver}
            onDrop={handleDrop}
        >
            {text}
        </div>
    );
};

export default Card;
