// Category.js

import React, { useRef, useState, useEffect } from 'react';
import Draggable from './Draggable';
import Droppable from './Droppable';

const Category = ({ position, text, cardMoving, mousePosition, onCardDropInsideDropZone }) => {
    const categoryRef = useRef();
    const thisSize = ({ x: 110, y: 100 });
    const dropZoneRef = useRef();
    const dropZoneSize = { x: 100, y: 70 };

    const [droppedCardList, setDroppedCardList] = useState([]);
    const [categoryData, setGameData] = useState({
        jobList: [],
        cardList: [],
        discardedCardsList: [],
        selectedCardIndex: null,
        lastSelectedCardIndex: null,
        categoryList: [],
        cardMoving: false,
    });

    const [isDropZoneActive, setIsDropZoneActive] = useState(false);

    const handleCardEnter = () => {
        console.log(`Card left ${text} dropZone!`);
        setIsDropZoneActive(true);
        console.log(droppedCardList);
    };

    const handleCardLeave = () => {
        console.log(`Card entered ${text} dropZone!`);
        setIsDropZoneActive(false);
    };


    useEffect(() => {
        if (isDropZoneActive) {
            onCardDropInsideDropZone(text);
            setIsDropZoneActive(false);
        }
    }, [cardMoving]);


    return (
        <Draggable>
            <div
                ref={categoryRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y + (10 * droppedCardList.length),
                    left: position.x + 'px',
                    top: position.y + 'px',

                    border: '1px solid black',
                    backgroundColor: 'cyan',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                <div>
                    {text}
                </div>
                <div>
                {droppedCardList.map((card, index) => (
                    <div key={index}>{card.text}</div>
                ))}
                </div>
                <Droppable
                    cardMoving={cardMoving}
                    mousePosition={mousePosition}
                    onCardEnter={handleCardEnter}
                    onCardLeave={handleCardLeave}
                >
                    <div
                        ref={dropZoneRef}
                        style={{
                            width: dropZoneSize.x,
                            height: dropZoneSize.y,
                            border: '1px solid black',
                            backgroundColor: !isDropZoneActive ? 'lightcyan' : 'transparent',
                            display: 'flex',
                            flexDirection: 'column',
                            justifyContent: 'space-around',
                            alignItems: 'center',
                        }}
                    >
                    </div>
                </Droppable>
            </div>
        </Draggable>
    );
};

export default Category;
