// Category.js

import React, { useRef, useState } from 'react';
import Draggable from './Draggable';
//import CardDropZone from './CardDropZone';
import Droppable from './Droppable';


const Category = ({ position, text, cardEnterDropZone, cardLeaveDropZone }) => {
    const categoryRef = useRef();
    const thisSize = ({ x: 110, y: 100 });

    const handleMouseEnter = () => {
        //console.log(`Mouse entering into ${text}`);
        cardEnterDropZone(text);
    };

    const handleMouseLeave = () => {
        //console.log(`Mouse leaving outo ${text}`);
        cardLeaveDropZone(text);
    };

    return (
        <Draggable>
            <div
                ref={categoryRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: position.x + 'px',
                    top: position.y + 'px',

                    // to adapt in css file
                    border: '1px solid black',
                    backgroundColor: 'lightcyan',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                {text}

                <Droppable>

                    <div
                        ref={dropZoneRef}
                        onDragOver={handleDragOver}
                        onDragLeave={handleDragLeave}
                        onDrop={handleDrop}
                        style={{
                            width: thisSize.x,
                            height: thisSize.y,
                            border: '1px solid black',
                            backgroundColor: isHovered ? 'transparent' : 'lightblue',
                            display: 'flex',
                            flexDirection: 'column',
                            justifyContent: 'space-around',
                            alignItems: 'center',
                        }}
                    >
                        dropZone
            </div>
                </Droppable>
            </div>
        </Draggable>
    );
};

export default Category;
