// InfoPanel.js

import React, { useRef, useState, useEffect } from 'react';
import Draggable from './Draggable';
import Droppable from './Droppable';

const InfoPanel = ({ position, onDragStart, cardMoving, mousePosition, onCardDropInsideDropZone, zIndexOrder }) => {
    const infoPanelRef = useRef();
    const thisSize = ({ x: 110, y: 120 });
    const dropZoneRef = useRef();
    const dropZoneSize = { x: 100, y: 70 };

    const [isDropZoneActive, setIsDropZoneActive] = useState(false);

    const handleCardEnter = () => {
        console.log(`Card entered infoPanel dropZone!`);
        setIsDropZoneActive(true);
    };

    const handleCardLeave = () => {
        console.log(`Card left infoPanel dropZone!`);
        setIsDropZoneActive(false);
    };


    useEffect(() => {
        if (isDropZoneActive) {
            onCardDropInsideDropZone("InfoPanel");
            setIsDropZoneActive(false);
        }
    }, [cardMoving]);

    return (
        <Draggable
            initialPosition={position}
            size={thisSize}
            onDragStart={onDragStart}
            zIndexOrder={zIndexOrder}
        >
            <div
                ref={infoPanelRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: position.x + 'px',
                    top: position.y + 'px',

                    border: '1px solid black',
                    backgroundColor: 'red',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                Discard

                <Droppable
                    cardMoving={cardMoving}
                    mousePosition={mousePosition}
                    onCardEnter={handleCardEnter}
                    onCardLeave={handleCardLeave}
                >
                    <div
                        style={{
                            width: dropZoneSize.x,
                            height: dropZoneSize.y,
                            border: '1px solid black',
                            backgroundColor: !isDropZoneActive ? 'pink' : 'transparent',
                            display: 'flex',
                            flexDirection: 'column',
                            justifyContent: 'space-around',
                            alignItems: 'center',
                        }}
                    >
                    </div>
                </Droppable>

                {
                    discardedCardsList.length > -1 && (
                        <button onClick={handleCancel} disabled={discardedCardsList.length === 0}>
                            Annuler
                        </button>
                    )
                }
            </div>
        </Draggable>
    );
};

export default InfoPanel;
