// CardDropZone.js

import React, { useState, useRef } from 'react';

const CardDropZone = ({ onDrop, isCardSelected }) => {
    const thisSize = { x: 100, y: 70 };
    const dropZoneRef = useRef();
    const [isHovered, setIsHovered] = useState(false);

    const handleDragOver = (e) => {
        e.preventDefault();
        setIsHovered(true);
    };

    const handleDragLeave = () => {
        setIsHovered(false);
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setIsHovered(false);
        onDrop(e);
    };

    return (
        <Droppable onDrop={handleDrop}>
            <div
                ref={dropZoneRef}
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
                style={{
                    width: thisSize.x,
                    height: thisSize.y,
                    border: '1px solid black',
                    backgroundColor: isHovered ? 'transparent' : 'lightblue',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                dropZone
        </div>
        </Droppable>
    );
};

export default CardDropZone;
