// CardDropZone.js

import React, { useEffect, useState } from 'react';

const CardDropZone = ({ onMouseEnter, onMouseLeave, mouseCoordinates }) => {
    const thisSize = ({ x: 100, y: 70 });
    const [isHovered, setIsHovered] = useState(false);

    useEffect(() => {
        if (isCardSelected) {
            const dropZoneX = parentPosition.x + x;
            const dropZoneY = parentPosition.y + y;

            const newIsActive =
                mouseX >= dropZoneX &&
                mouseX <= dropZoneX + width &&
                mouseY >= dropZoneY &&
                mouseY <= dropZoneY + height &&
                isCardSelected;

            if (newIsActive !== isActive) {
                setIsActive(newIsActive);

                if (onDropZoneChange) {
                    onDropZoneChange(newIsActive);
                }
            }
        }
    }, [mouseX, mouseY]);

    const handleMouseMove = (e) => {
        const rect = e.target.getBoundingClientRect();
        const mouseX = e.clientX - rect.left;
        const mouseY = e.clientY - rect.top;

        //console.log(mouseX, mouseY, rect.x, rect.y);

        if (
            mouseX >= 0 &&
            mouseX <= thisSize.x &&
            mouseY >= 0 &&
            mouseY <= thisSize.y
        ) {
            if (!isHovered) {
                setIsHovered(true);
                onMouseEnter();
            }
        } else {
            if (isHovered) {
                setIsHovered(false);
                onMouseLeave();
            }
        }
    };

    return (
        <div
            style={{
                width: thisSize.x,
                height: thisSize.y,

                // to adapt in css file
                border: '1px solid black',
                backgroundColor: isHovered ? 'transparent' : 'lightblue',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'space-around',
                alignItems: 'center',
            }}
        >
            dropZone

        </div>
    );
};

export default CardDropZone;
