// DragAndDropFramework.js

import React, { useState, useEffect } from 'react';

const DragAndDropFramework = () => {
    const [appState, setAppState] = useState({
        mouseX: 0,
        mouseY: 0,
        lastMouseX: 0,
        lastMouseY: 0,
        skipMoves: 0,
        // ... d'autres propriétés de l'état si nécessaire
    });

    useEffect(() => {
        const handleMouseMove = (e) => {
            const event = e.nativeEvent; // Utilisez nativeEvent pour obtenir l'événement brut de React
            setAppState((prevAppState) => ({
                ...prevAppState,
                lastMouseX: prevAppState.mouseX,
                lastMouseY: prevAppState.mouseY,
                mouseX: event.clientX,
                mouseY: event.clientY,
                skipMoves: (prevAppState.skipMoves || 0) + Math.abs(event.clientX - prevAppState.mouseX) + Math.abs(event.clientY - prevAppState.mouseY),
            }));
        };

        document.addEventListener('mousemove', handleMouseMove);

        return () => {
            document.removeEventListener('mousemove', handleMouseMove);
        };
    }, []); 

    return (
        <div>
            {React.Children.map(children, (child) => {
                if (React.isValidElement(child)) {
                    return React.cloneElement(child, { appState });
                }
                return child;
            })}
        </div>
    );
};

export default DragAndDropFramework;
