// InfoPanel.js

import React, { useRef, useState, useEffect } from 'react';
import Draggable from './Draggable';
import Droppable from './Droppable';
import Card from './Card';

import config from './../data/config';

const InfoPanel = ({ position, onDragStart, cardMoving, mousePosition, onCardDropInsideDropZone, zIndexOrder, droppedCard, onCardRestored }) => {
    const infoPanelRef = useRef();
    const thisSize = config.infoPanel.size;
    const cardSize = ({ x: 100, y: 70 });

    const dropZoneRef = useRef();
    const dropZoneSize = { x: 100, y: 70 };

    const [isDropZoneActive, setIsDropZoneActive] = useState(false);

    const handleCardEnter = () => {
        console.log(`Card entered infoPanel dropZone!`);
        setIsDropZoneActive(true);
    };

    const handleCardLeave = () => {
        console.log(`Card left infoPanel dropZone!`);
        setIsDropZoneActive(false);
    };

    useEffect(() => {
        if (isDropZoneActive) {
            onCardDropInsideDropZone("InfoPanel");
            setIsDropZoneActive(false);
        }
    }, [cardMoving]);

    const handleRecoverCard = () => {
        if (droppedCard) {
            const rect = infoPanelRef.current.getBoundingClientRect();
            const position = {
                x: rect.left + 5,
                y: rect.top + 35,
            };
            console.log(droppedCard.text);
            onCardRestored(position, droppedCard)
        }
    };

    return (
        <Draggable
            initialPosition={position}
            size={thisSize}
            onDragStart={onDragStart}
            zIndexOrder={zIndexOrder}
        >
            <div
                ref={infoPanelRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: position.x + 'px',
                    top: position.y + 'px',

                    border: '1px solid black',
                    backgroundColor: 'lightGreen',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                {config.infoPanel.title}

                {droppedCard ? (
                    <div>
                        <div
                            style={{
                                position: 'absolute',
                                top: -140 + 'px',
                                left: -12 + 'px',
                                width: thisSize.x * 1.2,
                                height: thisSize.y * 1.2,

                                border: '1px solid black',
                                backgroundColor: 'white',
                                display: 'flex',
                                flexDirection: 'column',
                                justifyContent: 'center',
                                alignItems: 'center',
                            }}
                        >
                            {droppedCard.description}
                        </div>

                        <div
                            onClick={handleRecoverCard}
                            style={{
                                width: cardSize.x,
                                height: cardSize.y,

                                border: '1px solid black',
                                backgroundColor: 'lightgray',
                                display: 'flex',
                                flexDirection: 'column',
                                justifyContent: 'center',
                                alignItems: 'center',
                                textAlign: 'center',
                            }}
                        >
                            {droppedCard.text}
                            <div style={{ fontSize: 'xx-small' }}>
                                {config.infoPanel.buttonText}
                            </div>
                        </div>
                    </div>
                ) : (
                        <Droppable
                            cardMoving={cardMoving}
                            mousePosition={mousePosition}
                            onCardEnter={handleCardEnter}
                            onCardLeave={handleCardLeave}
                        >
                            <div
                                style={{
                                    width: dropZoneSize.x,
                                    height: dropZoneSize.y,
                                    border: '1px solid black',
                                    backgroundColor: !isDropZoneActive ? 'green' : 'transparent',
                                    display: 'flex',
                                    flexDirection: 'column',
                                    justifyContent: 'space-around',
                                    alignItems: 'center',
                                }}
                            >
                            </div>
                        </Droppable>
                    )}
            </div>
        </Draggable>
    );
};

export default InfoPanel;




