import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

import config from './../data/config';

const JobNode = ({ job, onDelete, onSelect, selected }) => (
    <li className="job-node row-content">
        <span onClick={onSelect} className={`job-text ${selected ? 'job-text-selected' : ''}`}>
            {job}
        </span>
        <span className="delete-button button-small" onClick={onDelete}>
            {config.result.deleteJobButton}
        </span>
    </li>
);

const CategoryNode = ({ categoryName, jobs, onDeleteJob }) => {
    const [selectedJobs, setSelectedJobs] = useState([]);

    const handleSelectJob = (selectedJob) => {
        const updatedSelectedJobs = selectedJobs.includes(selectedJob)
            ? selectedJobs.filter((job) => job !== selectedJob)
            : [...selectedJobs, selectedJob];

        setSelectedJobs(updatedSelectedJobs);
    };

    const filteredJobs = jobs.filter((job) => selectedJobs.includes(job.text));

    return (
        <li className="category-node center-content">
            <div className="category-name">{categoryName}</div>
            <ul className="jobs-list">
                {filteredJobs.map((job, index) => (
                    <JobNode
                        key={index}
                        job={job.text}
                        onDelete={() => onDeleteJob(categoryName, job.text)}
                        onSelect={() => handleSelectJob(job.text)}
                        selected={selectedJobs.includes(job.text)}
                    />
                ))}
            </ul>
        </li>
    );
};

const ResultPage = ({ resultData }) => {
    const [resultTable, setResultTable] = useState([]);

    useEffect(() => {
        setResultTable(resultData);
    }, [resultData]);

    const handleDeleteJob = (categoryName, job) => {
        const updatedResultTable = resultTable.map((category) => {
            if (category.categoryName === categoryName) {
                return {
                    ...category,
                    jobs: category.jobs.filter((j) => j.text !== job),
                };
            }
            return category;
        });

        setResultTable(updatedResultTable);
    };

    const nonEmptyCategories = resultTable.filter((category) => category.jobs.length > 0);

    return (
        <div className="background-grid result-page center-content">
            <div className="center-content-spread">
                <div>
                    <h1>{config.result.title}</h1>
                    <h3>{config.result.subTitle}</h3>
                </div>
                <ul className="result-list">
                    {nonEmptyCategories &&
                        nonEmptyCategories.map((category, index) => (
                            <CategoryNode
                                key={index}
                                categoryName={category.categoryName}
                                jobs={category.jobs}
                                onDeleteJob={handleDeleteJob}
                            />
                        ))}
                </ul>
                <div className="ui-card ui-button-container">
                    <Link to="/tri-cartes/" className="button button-normal">
                        Retourner Ã  l'accueil
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default ResultPage;
