// Card.js

import React, { useState, useEffect } from 'react';

const Card = ({ text }) => {
    const [isDragging, setIsDragging] = useState(false);
    const [dragStartX, setDragStartX] = useState(0);
    const [dragStartY, setDragStartY] = useState(0);

    const handleMouseDown = (e) => {
        setIsDragging(true);
        setDragStartX(e.clientX);
        setDragStartY(e.clientY);
    };

    const handleMouseUp = () => {
        setIsDragging(false);
    };

    const handleMouseMove = (e) => {
        if (isDragging) {
            const deltaX = e.clientX - dragStartX;
            const deltaY = e.clientY - dragStartY;

            // Appliquer la translation avec translate3d
            const transformValue = `translate3d(${deltaX}px, ${deltaY}px, 0)`;
            e.currentTarget.style.transform = transformValue;
        }
    };

    useEffect(() => {
        // Ajouter les gestionnaires d'événements sur le document pour suivre le glisser-déposer global
        const handleDocumentMouseMove = (e) => {
            if (isDragging) {
                handleMouseMove(e);
            }
        };

        const handleDocumentMouseUp = () => {
            if (isDragging) {
                setIsDragging(false);
            }
        };

        document.addEventListener('mousemove', handleDocumentMouseMove);
        document.addEventListener('mouseup', handleDocumentMouseUp);

        return () => {
            // Nettoyer les gestionnaires d'événements lors du démontage du composant
            document.removeEventListener('mousemove', handleDocumentMouseMove);
            document.removeEventListener('mouseup', handleDocumentMouseUp);
        };
    }, [isDragging, handleMouseMove]);

    return (
        <div
            style={{
                width: 100,
                height: 70,
                border: '1px solid black',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                margin: '10px',
                cursor: 'grab', // Ajouter le curseur de la main pour indiquer la possibilité de glisser
            }}
            onMouseDown={handleMouseDown}
            onMouseUp={handleMouseUp}
        >
            {text}
        </div>
    );
};

export default Card;
