// DragAndDropFramework.js

import React, { useState, useEffect } from 'react';

const DragAndDropFramework = () => {
    const [appState, setAppState] = useState({
        mouseX: 0,
        mouseY: 0,
        lastMouseX: 0,
        lastMouseY: 0,
        skipMoves: 0,
        // ... autres propriétés de appState
    });

    const updateReducer = (e) => {
        // Assurez-vous que 'e' (l'événement) est défini avant d'accéder à ses propriétés
        if (e) {
            const event = e.nativeEvent || e; // Utilisez e.nativeEvent si nécessaire

            // Reste du code en utilisant event.clientX, event.clientY, etc.
            // ...
        }
    };

    useEffect(() => {
        const mouseMoveHandler = (e) => {
            // Assurez-vous que 'e' (l'événement) est défini avant d'accéder à ses propriétés
            if (e) {
                const event = e.nativeEvent || e; // Utilisez e.nativeEvent si nécessaire

                setAppState((prevState) => ({
                    ...prevState,
                    lastMouseX: prevState.mouseX,
                    lastMouseY: prevState.mouseY,
                    mouseX: event.clientX,
                    mouseY: event.clientY,
                    skipMoves: (prevState.skipMoves || 0) + Math.abs(event.clientX - prevState.lastMouseX) + Math.abs(event.clientY - prevState.lastMouseY),
                }));
            }
        };

        document.addEventListener('mousemove', mouseMoveHandler);

        return () => {
            document.removeEventListener('mousemove', mouseMoveHandler);
        };
    }, []);

    return (
        <div>
            {React.Children.map(children, (child) => {
                if (React.isValidElement(child)) {
                    return React.cloneElement(child, { appState });
                }
                return child;
            })}
        </div>
    );
};

export default DragAndDropFramework;
