// Droppable.js

import React, { useState } from 'react';

const Droppable = ({ children, onDragEnter, onDragLeave, onDrop }) => {
    const [isHovered, setIsHovered] = useState(false);

    const handleDragEnter = (e) => {
        e.preventDefault();
        if (isElementDragged(e)) {
            setIsHovered(true);
            if (onDragEnter) {
                onDragEnter();
            }
        }
    };

    const handleDragLeave = () => {
        setIsHovered(false);
        if (onDragLeave) {
            onDragLeave();
            setIsHovered(false);
        }
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setIsHovered(false);
        if (onDrop && isElementDragged(e)) {
            onDrop();
        }
    };

    const isElementDragged = (e) => {
        // Tu peux implémenter ici ta propre logique pour déterminer si un élément est en cours de drag
        // Par exemple, tu pourrais vérifier si le type de données que tu as défini pour le drag est présent dans l'événement
        return e.dataTransfer.types.includes('myCustomDragType');
    };

    return (
        <div
            onMouseEnter={handleDragEnter}
            onMouseLeave={handleDragLeave}
            onDrop={handleDrop}
            onDragOver={(e) => e.preventDefault()}
            style={{
                border: isHovered ? '2px dashed #aaa' : '2px solid #ddd',
                padding: '10px',
                marginBottom: '10px',
            }}
        >
            {children}
        </div>
    );
};

export default Droppable;
