// Category.js

import React, { useRef, useState, useEffect } from 'react';
import Draggable from './Draggable';
import Droppable from './Droppable';

const Category = ({ categoryIndex, position, text, onDragStart, cardMoving, mousePosition, onCardDropInsideDropZone, categoryCardList, onCardRestored, zIndexOrder}) => {
    const categoryRef = useRef();
    const [thisSize, setThisSize] = useState({ x: 110, y: 100 });
    const dropZoneRef = useRef();
    const dropZoneSize = { x: 100, y: 70 };

    const [isDropZoneActive, setIsDropZoneActive] = useState(false);
    const [isRenaming, setIsRenaming] = useState(false);
    const [categoryName, setCategoryName] = useState(text);

    const handleCardEnter = () => {
        //console.log(`Card left ${text} dropZone!`);
        setIsDropZoneActive(true);
    };

    const handleCardLeave = () => {
        //console.log(`Card entered ${text} dropZone!`);
        setIsDropZoneActive(false);
    };

    useEffect(() => {
        if (isDropZoneActive) {
            onCardDropInsideDropZone(text);
            setIsDropZoneActive(false);
        }
    }, [cardMoving]);

    useEffect(() => {
        setThisSize((prevSize) => ({ ...prevSize, y: prevSize.y + categoryCardList.length * 25 }));
    }, [categoryCardList]);

    const handleRenameClick = () => {
        setIsRenaming(true);
    };

    const handleRenameChange = (e) => {
        setCategoryName(e.target.value);
        const inputLength = e.target.value.length;
        setThisSize((prevSize) => ({ ...prevSize, y: 100 + categoryCardList.length * 25 }));
    };

    const handleRenameBlur = () => {
        setCategoryName((prevName) => categoryName);
        setIsRenaming(false);
    };
    const handleRenameKeyDown = (e) => {
        if (e.key === 'Enter') {
            handleRenameBlur();
        }
    };

    const handleCategoryCardClick = (categoryIndex, index) => {
        const rect = categoryRef.current.getBoundingClientRect();
        const position = {
            x: rect.left + rect.width / 2,
            y: rect.top,
        };

        onCardRestored(position, categoryIndex, index);

        setThisSize((prevSize) => ({ ...prevSize, y: 100 + categoryCardList.length * 30 }));
    }

    return (
        <Draggable
            initialPosition={position}
            size={thisSize}
            onDragStart={onDragStart}
            zIndexOrder={zIndexOrder}
        >
            <div
                ref={categoryRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: position.x + 'px',
                    top: position.y + 'px',

                    border: '1px solid black',
                    backgroundColor: 'cyan',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                <div
                >
                    {isRenaming ? (
                        <input
                            type="text"
                            value={categoryName}
                            onChange={handleRenameChange}
                            onBlur={handleRenameBlur}
                            onKeyDown={handleRenameKeyDown}
                            autoFocus
                            style={{
                                border: '0',
                                backgroundColor: 'transparent',
                                width: dropZoneSize.x,
                            }}
                        />
                    ) : (
                            <div
                                style={{
                                    display: 'flex',
                                    flexDirection: 'row',
                                    justifyContent: 'space-between',
                                    alignItems: 'start',
                                }}>
                                {categoryName}
                                <button onClick={handleRenameClick}></button>
                            </div>
                        )}
                </div>
                <div
                    style={{
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center',
                    }}
                >
                {categoryCardList.map((card, index) => (
                    <button
                        key={index}
                        onClick={() => handleCategoryCardClick(categoryIndex, index)}

                        style={{
                            height: 25,
                        }}
                    >
                        {card.text}</button>
                ))}
                </div>
                <Droppable
                    cardMoving={cardMoving}
                    mousePosition={mousePosition}
                    onCardEnter={handleCardEnter}
                    onCardLeave={handleCardLeave}
                >
                    <div
                        ref={dropZoneRef}
                        style={{
                            width: dropZoneSize.x,
                            height: dropZoneSize.y,
                            border: '1px solid black',
                            backgroundColor: !isDropZoneActive ? 'lightcyan' : 'transparent',
                            display: 'flex',
                            flexDirection: 'column',
                            justifyContent: 'space-around',
                            alignItems: 'center',
                        }}
                    >
                    </div>
                </Droppable>
            </div>
        </Draggable>
    );
};

export default Category;
