// CardDropZone.js

import React, { useRef } from 'react';

const CardDropZone = ({ onCardEnter, onCardLeave, onCardDrop }) => {
    const dropZoneRef = useRef();
    const thisSize = ({ x: 100, y: 70 });

    const handleDragEnter = (e) => {
        e.preventDefault();
        onCardEnter();
    };

    const handleDragLeave = (e) => {
        e.preventDefault();
        onCardLeave();
    };

    const handleDrop = (e) => {
        e.preventDefault();
        const cardText = e.dataTransfer.getData('text/plain');
        onCardDrop(cardText);
    };

    return (
        <div
            ref={dropZoneRef}

            onDragEnter={handleDragEnter}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}

            style={{
                width: thisSize.x,
                height: thisSize.y,

                // to adapt in css file
                border: '1px solid black',
                backgroundColor: 'lightblue',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'space-around',
                alignItems: 'center',
            }}
        >
            dropZone

        </div>
    );
};

export default CardDropZone;
