// GamePage.js
import React, { useState, useEffect } from 'react';
import Card from './Card';
import CardStack from './CardStack';
import jobListData from '../data/jobList.json';

const GamePage = () => {
    const [gameData, setGameData] = useState({
        jobList: [],
        cardList: [],
    });

    useEffect(() => {
        fetchJobList();
    }, []); // Le tableau vide signifie que cet effet se déclenchera uniquement après le montage initial.

    const fetchJobList = () => {
        const jobListString = JSON.stringify(jobListData);
        const jobListObject = JSON.parse(jobListString);

        setGameData((prevGameData) => ({
            ...prevGameData,
            jobList: Object.entries(jobListObject).map(([text, description]) => ({ text, description })),
        }));
    };

    const handleAddCard = () => {
        alert('pioche.');
    };

    return (
        <div>
            <CardStack addCard={handleAddCard} />
            {gameData.cardList.map((card) => (
                <Card key={card.id} text={card.text} />
            ))}
        </div>
    );
};

export default GamePage;
