// Droppable.js

import React, { useState, useEffect } from 'react';

const Droppable = ({ children, cardMoving }) => {

    useEffect(() => {
        const handleMouseMove = (e) => {
            if (isDragging) {
                const deltaX = e.clientX - dragStartX + currentOffset.x;
                const deltaY = e.clientY - dragStartY + currentOffset.y;

                const transformValue = `translate3d(${deltaX}px, ${deltaY}px, 0)`;
                targetRef.current.style.transform = transformValue;
                if (onDragMove)
                    onDragMove();
            }
        };

        const handleMouseUp = () => {
            setIsDragging(false);
            targetRef.current.style.zIndex = '1';
            if (onDragEnd)
                onDragEnd();
        };

        if (isDragging) {
            window.addEventListener('mousemove', handleMouseMove);
            window.addEventListener('mouseup', handleMouseUp);
        }

        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
            window.removeEventListener('mouseup', handleMouseUp);
        };
    }, [isDragging]);

    return (
        <div
        >
            {children}
        </div>
    );
};

export default Droppable;
