// Droppable.js

import React, { useRef, useState, useEffect } from 'react';

const Droppable = ({ children, cardMoving, mousePosition }) => {
    const droppableRef = useRef(null);
    const [isMouseOverZone, setIsMouseOverZone] = useState(false);

    useEffect(() => {
        if (cardMoving && mousePosition) {
            const droppableElement = droppableRef.current;

            if (droppableElement) {
                console.log(isMouseOverZone);

                const rect = droppableElement.getBoundingClientRect();
                const isCardDroppable = (
                    mousePosition.x >= rect.left &&
                    mousePosition.x <= rect.right &&
                    mousePosition.y >= rect.top &&
                    mousePosition.y <= rect.bottom
                );

                setIsMouseOverZone(isCardDroppable);
            }
        }
    }, [mousePosition]);

    return (
        <div
            ref={droppableRef}
        >
            {children}
        </div>
    );
};

export default Droppable;
