// InfoPanel.js

import React, { useRef, useState, useEffect } from 'react';
import Draggable from './Draggable';
import Droppable from './Droppable';

const InfoPanel = ({ position, onDragStart, cardMoving, mousePosition, onCardDropInsideDropZone, zIndexOrder, droppedCard, onCardRestored}) => {
    const infoPanelRef = useRef();
    const thisSize = ({ x: 110, y: 120 });
    const dropZoneRef = useRef();
    const dropZoneSize = { x: 100, y: 70 };

    const [isDropZoneActive, setIsDropZoneActive] = useState(false);

    const handleCardEnter = () => {
        console.log(`Card entered infoPanel dropZone!`);
        setIsDropZoneActive(true);
    };

    const handleCardLeave = () => {
        console.log(`Card left infoPanel dropZone!`);
        setIsDropZoneActive(false);
    };

    useEffect(() => {
        if (isDropZoneActive) {
            onCardDropInsideDropZone("InfoPanel");
            setIsDropZoneActive(false);
        }
    }, [cardMoving]);

    const handleRecoverCard = () => {
        if (droppedCard) {
            console.log(droppedCard.text);
            onCardRestored(droppedCard)
        }
    };

    return (
        <Draggable
            initialPosition={position}
            size={thisSize}
            onDragStart={onDragStart}
            zIndexOrder={zIndexOrder}
        >
            <div
                ref={infoPanelRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: position.x + 'px',
                    top: position.y + 'px',

                    border: '1px solid black',
                    backgroundColor: 'lightGreen',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                Info Panel

                {droppedCard ? (
                    <div>
            <div
                        style={{
                            position: 'absolute',
                            width: thisSize.x * 2,
                            height: thisSize.y * 2,
                            top: -200 + 'px',
                            left: -55 + 'px',

                            border: '1px solid black',
                            backgroundColor: 'lightGreen',
                            display: 'flex',
                            flexDirection: 'column',
                            justifyContent: 'space-around',
                            alignItems: 'center',
                        }}
                    >
                        <br />
                        {droppedCard.description}
                    </div>
                    <div
                        onClick={handleRecoverCard}
                    >
                        {droppedCard.text}
                    </div>
                    </div>
                ) : (
                        <Droppable
                            cardMoving={cardMoving}
                            mousePosition={mousePosition}
                            onCardEnter={handleCardEnter}
                            onCardLeave={handleCardLeave}
                        >
                            <div
                                style={{
                                    width: dropZoneSize.x,
                                    height: dropZoneSize.y,
                                    border: '1px solid black',
                                    backgroundColor: !isDropZoneActive ? 'green' : 'transparent',
                                    display: 'flex',
                                    flexDirection: 'column',
                                    justifyContent: 'space-around',
                                    alignItems: 'center',
                                }}
                            >
                            </div>
                        </Droppable>
                    )}
            </div>
        </Draggable>
    );
};

export default InfoPanel;
