// Category.js

import React, { useRef, useState, useEffect } from 'react';
import Draggable from './Draggable';
import Droppable from './Droppable';

const Category = ({ position, text, cardMoving, mousePosition, onCardDropInsideDropZone, categoryCardList}) => {
    const categoryRef = useRef();
    const thisSize = ({ x: 110, y: 100 });
    const dropZoneRef = useRef();
    const dropZoneSize = { x: 100, y: 70 };

    const [isDropZoneActive, setIsDropZoneActive] = useState(false);
    const [isRenaming, setIsRenaming] = useState(false);

    const handleCardEnter = () => {
        console.log(`Card left ${text} dropZone!`);
        setIsDropZoneActive(true);
        console.log(categoryCardList);
    };

    const handleCardLeave = () => {
        console.log(`Card entered ${text} dropZone!`);
        setIsDropZoneActive(false);
    };


    useEffect(() => {
        if (isDropZoneActive) {
            onCardDropInsideDropZone(text);
            setIsDropZoneActive(false);
        }
    }, [cardMoving]);


    const handleRenameClick = () => {
        setIsRenaming(true);
    };

    const handleRenameChange = (e) => {
        setNewCategoryName(e.target.value);
    };
    const handleRenameBlur = () => {
        // Logique pour valider le nouveau nom et désactiver le mode de renommage
        setCategoryData((prevCategoryData) => ({
            ...prevCategoryData,
            text: newCategoryName,
        }));
        setIsRenaming(false);
    };

    return (
        <Draggable>
            <div
                ref={categoryRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y + (20 * categoryCardList.length),
                    left: position.x + 'px',
                    top: position.y + 'px',

                    border: '1px solid black',
                    backgroundColor: 'cyan',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                <div>
                    {isRenaming ? (
                        <input
                            type="text"
                            value={newCategoryName}
                            onChange={handleRenameChange}
                            onBlur={handleRenameBlur}
                        />
                    ) : (
                            <div>
                                {text}
                                <button onClick={handleRenameClick}>Renommer</button>
                            </div>
                        )}
                </div>
                <div>
                {categoryCardList.map((card, index) => (
                    <button key={index}>{card.text}</button>
                ))}
                </div>
                <Droppable
                    cardMoving={cardMoving}
                    mousePosition={mousePosition}
                    onCardEnter={handleCardEnter}
                    onCardLeave={handleCardLeave}
                >
                    <div
                        ref={dropZoneRef}
                        style={{
                            width: dropZoneSize.x,
                            height: dropZoneSize.y,
                            border: '1px solid black',
                            backgroundColor: !isDropZoneActive ? 'lightcyan' : 'transparent',
                            display: 'flex',
                            flexDirection: 'column',
                            justifyContent: 'space-around',
                            alignItems: 'center',
                        }}
                    >
                    </div>
                </Droppable>
            </div>
        </Draggable>
    );
};

export default Category;
