// GamePage.js

import React from 'react';
import Card from './Card';
import CardStack from './CardStack';


const GamePage = () => {
    const [cardList, setCardList] = useState([
        { id: 1, text: 'Métier 1' },
        { id: 2, text: 'Métier 2' },
    ]);

    return (
        <div>
            <CardStack  />
            {cardList.map((card) => (
                <Card key={card.id} text={card.text} isDrawn={card.isDrawn} />
            ))}
        </div>
    );
};

export default GamePage;
