// CardDiscard.js

import React, { useRef, useState } from 'react';
import Draggable from './Draggable';
import Droppable from './Droppable';

const CardDiscard = ({ position, cardEnterDropZone, cardLeaveDropZone}) => {
    const discardRef = useRef();
    const thisSize = ({ x: 110, y: 100 });
    const dropZoneRef = useRef();
    const dropZoneSize = { x: 100, y: 70 };

    const handleMouseEnter = () => {
        //console.log(`Mouse entering into discard`);
        cardEnterDropZone("discard");
    };

    const handleMouseLeave = () => {
        //console.log(`Mouse leaving outo discard`);
        cardLeaveDropZone("discard");
    };

    return (
        <Draggable>
            <div
                ref={discardRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: position.x + 'px',
                    top: position.y + 'px',

                    border: '1px solid black',
                    backgroundColor: 'red',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                Discard


                <Droppable>
                    <div
                        style={{
                            width: dropZoneSize.x,
                            height: dropZoneSize.y,
                            border: '1px solid black',
                            backgroundColor: 'lightgrey',
                            display: 'flex',
                            flexDirection: 'column',
                            justifyContent: 'space-around',
                            alignItems: 'center',
                        }}
                    >
                        Discard dropZone
                    </div>
                </Droppable>
            </div>
        </Draggable>
    );
};

export default CardDiscard;
