// HomePage.js

import React, { useState } from 'react';

const HomePage = ({ onGoogleSheetSubmit }) => {
    const [googleSheetURL, setGoogleSheetURL] = useState('');

    const handleURLChange = (event) => {
        setGoogleSheetURL(event.target.value);
    };

    const handleSubmit = () => {
        onGoogleSheetSubmit(googleSheetURL);
    };

    return (
        <div className="home-page">
            <div className="center-content">
                <h1>Bienvenue dans le Jeu de Tri de Cartes</h1>
                <Link to="/game">
                    <button>Commencer</button>
                </Link>
                <h2>Coller l'URL du Google Sheet :</h2>
                <input
                    type="text"
                    value={googleSheetURL}
                    onChange={handleURLChange}
                    placeholder="Collez l'URL ici"
                />
                <button onClick={handleSubmit}>Valider</button>
            </div>
        </div>
    );
};

export default HomePage;
