// Card.js

import React from 'react';
import Draggable from './Draggable';

const Card = ({ id, text, position, onDrop }) => {
    const thisSize = ({ x: 100, y: 70 });

    const handleDragStart = (e) => {
        e.dataTransfer.setData('cardId', this);
    };

    return (
        <Draggable onDrop={onDrop}>
            <div
                onDragStart={handleDragStart}
                style={{
                    position: 'absolute',
                    left: position.x + 'px',
                    top: position.y + 'px',
                    width: thisSize.x,
                    height: thisSize.y,

                    // to adapt in css file
                    border: '1px solid black',
                    backgroundColor: 'lightgray',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    textAlign: 'center',
                    userSelect: 'none',
                }}
            >
                {text}
            </div>
        </Draggable>
    );
};

export default Card;
