// Category.js

import React, { useRef, useState } from 'react';
import Draggable from './Draggable';
import CardDropZone from './CardDropZone';


const Category = ({ position, text, cardEnterDropZone, cardLeaveDropZone, mouseCoordinates }) => {
    const categoryRef = useRef();
    const thisSize = ({ x: 110, y: 100 });

    const handleMouseEnter = () => {
        //console.log(`Mouse entering into ${text}`);
        cardEnterDropZone(categoryRef);
    };

    const handleMouseLeave = () => {
        //console.log(`Mouse leaving outo ${text}`);
        cardLeaveDropZone(categoryRef);
    };

    return (
        <Draggable>
            <div
                ref={categoryRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: position.x + 'px',
                    top: position.y + 'px',

                    // to adapt in css file
                    border: '1px solid black',
                    backgroundColor: 'lightcyan',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                {text}

                <CardDropZone
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                    mouseCoordinates={mouseCoordinates}
                />
            </div>
        </Draggable>
    );
};

export default Category;
