// Category.js

import React, { useRef } from 'react';
import Draggable from './Draggable';

const Category = ({ }) => {
    const categoryRef = useRef();
    const thisSize = ({ x: 110, y: 80 });

    return (
        <Draggable>
            <div
                ref={categoryRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: thisSize.x / 2 + 'px',
                    top: window.innerHeight / 2 - thisSize.y / 2 + 'px',

                    // to adapt in css file
                    border: '1px solid black',
                    backgroundColor: 'lightcyan',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                Category
            </div>
        </Draggable>
    );
};

export default Category;
