// config.js

const config = {
    pile: {
        title: "deck",
        buttonText: "piocher",
        thisSize: ({ x: 110, y: 80 }),
    },
    discard: {
        title: "Discard",
        buttonText: "annuler",
    },
    infoPanel: {
        title: "Informations",
        buttonText: "Cliquer pour récupérer",
    },
};

export default config;
