// CardPile.js

import React, { useRef } from 'react';
import Draggable from './Draggable';

const CardPile = ({ position, onDragStart, addCard, counter, zIndexOrder}) => {
    const cardPileRef = useRef();
    const thisSize = ({ x: 110, y: 80 });

    const handleAddCard = () => {
        const rect = cardPileRef.current.getBoundingClientRect();
        const pilePosition = {
            x: rect.left + rect.width / 2,
            y: rect.top,
        };
        addCard(pilePosition);
    };

    return (
        <Draggable
            initialPosition={position}
            size={thisSize}
            onDragStart={onDragStart}
            zIndexOrder={zIndexOrder}
        >
            <div
                ref={cardPileRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: position.x + 'px',
                    top: position.y + 'px',

                    border: '1px solid black',
                    backgroundColor: 'lightyellow',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                Deck
                <button onClick={handleAddCard} disabled={counter === 0}>pioche : {counter}</button>
            </div>
        </Draggable>
    );
};

export default CardPile;
