// DragAndDropFramework.js

import React, { useState, useEffect } from 'react';

const DragAndDropFramework = ({ children }) => {
    const [appState, setAppState] = useState({
        mouseX: 0,
        mouseY: 0,
        lastMouseX: 0,
        lastMouseY: 0,
        skipMoves: 0,
        // ... d'autres états selon votre besoin
    });

    useEffect(() => {
        const handleMouseMove = (e) => {
            const event = e.nativeEvent;
            setAppState((prevState) => ({
                ...prevState,
                lastMouseX: prevState.mouseX,
                lastMouseY: prevState.mouseY,
                mouseX: event.clientX,
                mouseY: event.clientY,
                skipMoves: (prevState.skipMoves || 0) + Math.abs(prevState.mouseX - prevState.lastMouseX) + Math.abs(prevState.mouseY - prevState.lastMouseY),
            }));
        };

        document.addEventListener('mousemove', handleMouseMove);

        return () => {
            document.removeEventListener('mousemove', handleMouseMove);
        };
    }, []);

    return (
        <div>
            {React.Children.map(children, (child) => {
                if (React.isValidElement(child)) {
                    return React.cloneElement(child, { appState });
                }
                return child;
            })}
        </div>
    );
};

export default DragAndDropFramework;
