import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import MindMap from 'react-mindmap';

import config from './../data/config';

const ResultPage = ({ resultData }) => {
    const [mindmapData, setMindmapData] = useState({ nodes: [], links: [] });

    useEffect(() => {
        const generateMindmapData = () => {
            const nodes = [
                { id: 'user', label: 'Utilisateur', fill: '#3498db' },
                ...resultData.map((category, index) => ({
                    id: `category${index}`,
                    label: category.categoryName,
                    fill: '#2ecc71',
                })),
                ...resultData.flatMap((category, index) => category.jobs.map((job) => ({
                    id: `job-${job}`,
                    label: job,
                    fill: '#e74c3c',
                }))),
            ];

            const links = [
                ...resultData.map((_, index) => ({
                    source: 'user',
                    target: `category${index}`,
                })),
                ...resultData.flatMap((category, index) => category.jobs.map((job) => ({
                    source: `category${index}`,
                    target: `job-${job}`,
                }))),
            ];

            return { nodes, links };
        };

        setMindmapData(generateMindmapData());
    }, [resultData]);

    return (
        <div className="background-grid result-page center-content">
            <div className="center-content">
                <h1>{config.result.title}</h1>
                <h3>{config.result.subTitle}</h3>
                <MindMap
                    nodes={mindmapData.nodes}
                    links={mindmapData.links}
                    root={mindmapData.nodes[0]}
                />
                <div className="ui-card ui-button-container">
                    <Link to="/tri-cartes/" className="button button-normal">
                        Retourner Ã  l'accueil
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default ResultPage;
