// CardDiscard.js

import React, { useRef, useState, useEffect } from 'react';
import Draggable from './Draggable';
import Droppable from './Droppable';

const CardDiscard = ({ position, cardMoving, mousePosition, onCardDropInsideDropZone, discardedCardsList}) => {
    const discardRef = useRef();
    const thisSize = ({ x: 110, y: 100 });
    const dropZoneRef = useRef();
    const dropZoneSize = { x: 100, y: 70 };

    const [isDropZoneActive, setIsDropZoneActive] = useState(false);

    const handleCardEnter = () => {
        console.log(`Card entered discard dropZone!`);
        setIsDropZoneActive(true);
    };

    const handleCardLeave = () => {
        console.log(`Card left discard dropZone!`);
        setIsDropZoneActive(false);
    };


    useEffect(() => {
        if (isDropZoneActive) {
            onCardDropInsideDropZone("Discard");
            setIsDropZoneActive(false);
        }
    }, [cardMoving]);

    return (
        <Draggable>
            <div
                ref={discardRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: position.x + 'px',
                    top: position.y + 'px',

                    border: '1px solid black',
                    backgroundColor: 'red',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                Discard

                 <button> annuler </button>
                <Droppable
                    cardMoving={cardMoving}
                    mousePosition={mousePosition}
                    onCardEnter={handleCardEnter}
                    onCardLeave={handleCardLeave}
                >
                    <div
                        style={{
                            width: dropZoneSize.x,
                            height: dropZoneSize.y,
                            border: '1px solid black',
                            backgroundColor: !isDropZoneActive ? 'pink' : 'transparent',
                            display: 'flex',
                            flexDirection: 'column',
                            justifyContent: 'space-around',
                            alignItems: 'center',
                        }}
                    >
                    </div>
                </Droppable>
            </div>
        </Draggable>
    );
};

export default CardDiscard;
