    const [cardList, setCardList] = useState([
        { id: 1, text: 'Métier 1' },
        { id: 2, text: 'Métier 2' },
    ]);

    const drawCard = () => {
        if (cardList.length > 0) {
            // Piochez la carte du dessus du deck
            const drawnCard = cardList.pop();

            // Mettez à jour la liste des cartes et ajoutez la carte à la position du CardStack
            setCardList([...cardList]);
            setCardList((prev) => [...prev, { ...drawnCard, isDrawn: true }]);
