// Card.js

import React from 'react';
import Draggable from './Draggable';

const Card = ({ text, position }) => {
    const left = position && position.x ? position.x : 0;
    const top = position && position.y ? position.y : 0;
    return (
        <Draggable>
            <div
                style={{
                    left: position.x,
                    top: position.y,
                    width: 100,
                    height: 70,
                    border: '1px solid black',
                    backgroundColor: 'lightgray',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                }}
            >
                {text}
            </div>
        </Draggable>
    );
};

export default Card;
