import React, { useState } from 'react';
import Card from './Card';
import CardStack from './CardStack';


const GamePage = () => {
    state = {
        mouseX: 0,
        mouseY: 0,

        jobList: [],

        cards: [],
        selectedCardIndex: null,

        categories: [],

        isDiscardDropZoneActive: false,
        discardedCards: [],

        isinfoPanelDropZoneActive: false,
        isInfoPanelActive: false,
        activeInfoPanelCard: null,
        infoPanelPosition: null,
        descriptionText: "",

        cursor: 'pointer',
    };
    const [cardList, setCardList] = useState([]);

    const handleAddCard = () => {
        alert('pioche.');
    };

    return (
        <div>
            <CardStack addCard={handleAddCard}/>
            {cardList.map((card) => (
                <Card key={card.id} text={card.text} />
            ))}
        </div>
    );
};

export default GamePage;
