// Card.js

import React from 'react';
import Draggable from './Draggable';

const Card = ({ text, position, onDragStart, onDragEnd, onDragMove }) => {
    const thisSize = ({ x: 100, y: 70 });

    return (
        <Draggable
            onDragStart={() => onDragStart(targetRef)}
            onDragEnd={onDragEnd}
            onDragMove={onDragMove}
        >
            <div
                style={{
                    position: 'absolute',
                    left: position.x + 'px',
                    top: position.y + 'px',
                    width: thisSize.x,
                    height: thisSize.y,

                    border: '1px solid black',
                    backgroundColor: 'lightgray',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    textAlign: 'center',
                    userSelect: 'none',
                }}
            >
                {text}
            </div>
        </Draggable>
    );
};

export default Card;
