import React, { useState, useEffect } from 'react';
import Draggable from './Draggable';

const Card = ({ text, position }) => {
    return (
        <Draggable>
            <div
                style={{
                    position: 'absolute',
                    left: position.x + 'px',
                    top: position.y + 'px',
                    width: 100,
                    height: 70,
                    border: '1px solid black',
                    backgroundColor: 'lightgray',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                }}
            >
                {text}
            </div>
            <div
                onDrop={onDrop}
                onDragOver={(e) => e.preventDefault()}
                style={{
                    width: '100%',
                    height: '100%',
                    position: 'absolute',
                    top: 0,
                    left: 0,
                }}
            />
        </Draggable>
    );
};

export default Card;
