// Droppable.js

import React, { useState } from 'react';

const Droppable = ({ children }) => {
    const [isHovered, setIsHovered] = useState(false);

    const handleDragOver = (e) => {
        e.preventDefault();
        setIsHovered(true);
        // Ajoutez votre logique personnalisée onDragOver ici
    };

    const handleDragLeave = () => {
        setIsHovered(false);
        // Ajoutez votre logique personnalisée onDragLeave ici
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setIsHovered(false);
        // Ajoutez votre logique personnalisée onDrop ici
    };

    return (
        <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            style={{
                width: '100%',
                height: '100%',
                position: 'relative',
            }}
        >
            {isHovered && (
                <div
                    style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%',
                        background: 'rgba(0, 0, 0, 0.2)',
                        zIndex: 1,
                    }}
                />
            )}
            {children}
        </div>
    );
};

export default Droppable;
