// config.js

const config = {
    pile: {
        title: "deck",
        buttonText: "piocher",

        size: ({ x: 110, y: 80 }),
    },
    discard: {
        title: "Discard",
        buttonText: "annuler",

        size: ({ x: 110, y: 120 }),
    },
    infoPanel: {
        title: "Informations",
        buttonText: "Cliquer pour récupérer",

        size: ({ x: 110, y: 220 }),
    },
};

export default config;
