// CardDropZone.js

import React, { useEffect, useState, useRef } from 'react';

const CardDropZone = ({ onMouseEnter, onMouseLeave, mouseCoordinates }) => {
    const thisSize = ({ x: 100, y: 70 });
    const dropZoneRef = useRef();
    const [isHovered, setIsHovered] = useState(false);

    useEffect((e) => {
        const dropZoneRect = dropZoneRef.current.getBoundingClientRect();
        const mouseX = mouseCoordinates.x;
        const mouseY = mouseCoordinates.y;

        const isMouseOver =
            mouseX >= dropZoneRect.left &&
            mouseX <= dropZoneRect.right &&
            mouseY >= dropZoneRect.top &&
            mouseY <= dropZoneRect.bottom;

        if (isMouseOver) {
            if (!isHovered) {
                setIsHovered(true);
                onMouseEnter();
            }
        } else {
            if (isHovered) {
                setIsHovered(false);
                onMouseLeave();
            }
        }
    }, [mouseCoordinates]);

    return (
        <div
            style={{
                width: thisSize.x,
                height: thisSize.y,

                // to adapt in css file
                border: '1px solid black',
                backgroundColor: isHovered ? 'transparent' : 'lightblue',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'space-around',
                alignItems: 'center',
            }}
        >
            dropZone

        </div>
    );
};

export default CardDropZone;
