import React, { useState, useEffect, useRef } from 'react';

const Draggable = ({ children }) => {
    const [isDragging, setIsDragging] = useState(false);
    const [dragStartX, setDragStartX] = useState(0);
    const [dragStartY, setDragStartY] = useState(0);
    const [selected, setSelected] = useState(false);

    const targetRef = useRef(null);

    useEffect(() => {
        const handleMouseMove = (e) => {
            if (isDragging) {
                const deltaX = e.clientX - dragStartX;
                const deltaY = e.clientY - dragStartY;

                const currentTransform = targetRef.current.style.transform;

                const transformValue = `translate3d(${deltaX}px, ${deltaY}px, 0)`;
                targetRef.current.style.transform = transformValue;

                // Mettre l'élément sélectionné au premier plan
                targetRef.current.style.zIndex = '2';
            }
        };

        const handleMouseUp = () => {
            setIsDragging(false);
            // Réinitialiser le z-index après le relâchement de la souris
            targetRef.current.style.zIndex = '1';
        };

        if (isDragging) {
            window.addEventListener('mousemove', handleMouseMove);
            window.addEventListener('mouseup', handleMouseUp);
        }

        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
            window.removeEventListener('mouseup', handleMouseUp);
        };
    }, [isDragging, dragStartX, dragStartY]);

    const handleMouseDown = (e) => {
        setIsDragging(true);
        setDragStartX(e.clientX);
        setDragStartY(e.clientY);

        // Stocker la référence de l'élément cible
        targetRef.current = e.currentTarget;

        // Marquer comme élément sélectionné
        setSelected(true);

        // Mettre l'élément sélectionné au premier plan
        targetRef.current.style.zIndex = '2';
    };

    return (
        <div
            onMouseDown={handleMouseDown}
            style={{
                cursor: isDragging ? 'grabbing' : 'grab',
                userSelect: 'none', // Empêcher la sélection du texte
            }}
        >
            {children}
        </div>
    );
};

export default Draggable;
