import React, { useRef, useState } from 'react';

const Draggable = ({ children, onDragStart, onDragEnd }) => {
    const [isDragging, setIsDragging] = useState(false);
    const dragItem = useRef(null);

    const handleDragStart = (e) => {
        setIsDragging(true);
        dragItem.current = e.target;
        e.dataTransfer.setData('text/plain', ''); // Définir des données (peu importe ici)
        if (onDragStart) {
            onDragStart(e);
        }
    };

    const handleDragEnd = (e) => {
        setIsDragging(false);
        dragItem.current = null;
        if (onDragEnd) {
            onDragEnd(e);
        }
    };

    return (
        <div
            draggable
            onDragStart={handleDragStart}
            onDragEnd={handleDragEnd}
            style={{
                cursor: isDragging ? 'grabbing' : 'grab',
            }}
        >
            {children}
        </div>
    );
};

const Droppable = ({ onDragOver, onDragLeave, onDrop, children }) => {
    const handleDragOver = (e) => {
        e.preventDefault();
        if (onDragOver) {
            onDragOver(e);
        }
    };

    const handleDragLeave = (e) => {
        if (onDragLeave) {
            onDragLeave(e);
        }
    };

    const handleDrop = (e) => {
        e.preventDefault();
        if (onDrop) {
            onDrop(e);
        }
    };

    return (
        <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            style={{
                border: '2px dashed #aaa',
                padding: '10px',
                margin: '10px',
            }}
        >
            {children}
        </div>
    );
};

const App = () => {
    const handleDragOver = (e) => {
        console.log('Drag Over');
    };

    const handleDragLeave = (e) => {
        console.log('Drag Leave');
    };

    const handleDrop = (e) => {
        console.log('Drop');
    };

    return (
        <div>
            <Droppable onDragOver={handleDragOver} onDragLeave={handleDragLeave} onDrop={handleDrop}>
                <Draggable>
                    <p>Drag Me!</p>
                </Draggable>
            </Droppable>
        </div>
    );
};

export default App;
