// Card.js

import React from 'react';
import Draggable from './Draggable';

const Card = ({ text }) => {
    return (
        <Draggable>
            <div
                style={{
                    width: 100,
                    height: 70,
                    border: '1px solid black',
                    backgroundColor: 'lightgray',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    margin: '10px',
                }}
            >
                {text}
            </div>
        </Draggable>
    );
};

export default Card;
