// config.js

const config = {
    home: {
        title: "Bienvenue 100 métiers",
        buttonStart: "commencer",
        arrayTitle: "deck",
        arraySubTitle: "le jeu de carte joyeux",
        arrayButton: "deck",
    },
    card: {
        size: ({ x: 100, y: 70 }),
    },
    pile: {
        title: "deck",
        buttonText: "piocher",

        size: ({ x: 110, y: 80 }),
    },
    discard: {
        title: "Discard",
        buttonText: "annuler",

        size: ({ x: 110, y: 120 }),
    },
    infoPanel: {
        title: "Informations",
        buttonText: "Cliquer pour récupérer",

        size: ({ x: 110, y: 120 }),
    },
    category: {
        baseTitle: "catégorie ",
        createButtonText: "créer une catégorie",
    },
};

export default config;
