// CardDropZone.js

import React, { useRef } from 'react';

const CardDropZone = ({ onCardDrop }) => {
    const categoryRef = useRef();
    const thisSize = ({ x: 100, y: 70 });

    const handleDrop = (e) => {
        e.preventDefault();
        const cardId = e.dataTransfer.getData('cardId');

        // Appelez la fonction de gestion onCardDrop avec l'ID de la carte
        if (onCardDrop) {
            onCardDrop(cardId);
        }
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    return (
        <div
            ref={categoryRef}

            onDrop={handleDrop}
            onDragOver={handleDragOver}

            style={{
                width: thisSize.x,
                height: thisSize.y,

                // to adapt in css file
                border: '1px solid black',
                backgroundColor: 'lightblue',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'space-around',
                alignItems: 'center',
            }}
        >
            dropZone

        </div>
    );
};

export default CardDropZone;
