// CardDiscard.js

import React, { useRef, useState } from 'react';
import Draggable from './Draggable';
import CardDropZone from './CardDropZone';


const CardDiscard = ({ position, cardEnterDropZone, cardLeaveDropZone}) => {
    const discardRef = useRef();
    const thisSize = ({ x: 110, y: 100 });

    const handleMouseEnter = () => {
        //console.log(`Mouse entering into discard`);
        cardEnterDropZone("discard");
    };

    const handleMouseLeave = () => {
        //console.log(`Mouse leaving outo discard`);
        cardLeaveDropZone("discard");
    };

    return (
        <Draggable>
            <div
                ref={discardRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: position.x + 'px',
                    top: position.y + 'px',

                    // to adapt in css file
                    border: '1px solid black',
                    backgroundColor: 'red',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                Discard

                <CardDropZone
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                />
            </div>
        </Draggable>
    );
};

export default CardDiscard;
