// Card.js

import React, { useRef } from 'react';
import Draggable from './Draggable';

const Card = ({ text, position, onDragStart, onDragEnd, onCollisionCheck }) => {
    const cardRef = useRef();
    const thisSize = ({ x: 100, y: 70 });

    return (
        <Draggable
            onDragStart={onDragStart}
            onDragEnd={onDragEnd}
            onCollisionCheck={() => onCollisionCheck(cardRef.current)}
        >
            <div
                style={{
                    ref:{ cardRef },
                    position: 'absolute',
                    left: position.x + 'px',
                    top: position.y + 'px',
                    width: thisSize.x,
                    height: thisSize.y,

                    // to adapt in css file
                    border: '1px solid black',
                    backgroundColor: 'lightgray',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    textAlign: 'center',
                    userSelect: 'none',
                }}
            >
                {text}
            </div>
        </Draggable>
    );
};

export default Card;
