// GamePage.js
import React, { useState, useEffect } from 'react';
import Card from './Card';
import CardPile from './CardPile';
import CardDropZone from './CardDropZone';
import Category from './Category';
import jobListData from './../data/jobList.json';
import CardDiscard from './CardDiscard';

const GamePage = () => {
    const [gameData, setGameData] = useState({
        jobList: [],
        cardList: [],
        selectedCardIndex: null,
        categoryList: [],
    });
    const isCardPileDisabled = gameData.jobList.length === 0;
    const discardPosition = ({ x: 110, y: 80 });

    useEffect(() => {
        if (gameData.jobList.length <= 0) {
            fetchJobList();
        }
    }, []);

    const fetchJobList = () => {
        const jobListString = JSON.stringify(jobListData);
        const jobListObject = JSON.parse(jobListString);

        setGameData((prevGameData) => ({
            ...prevGameData,
            jobList: Object.entries(jobListObject).map(([text, description]) => ({ text, description })),
        }));
        console.log("fetchJobList");
    };

    const handleAddCard = (pilePosition) => {
        const { jobList } = gameData;

        if (jobList.length === 0) return;

        const randomIndex = Math.floor(Math.random() * jobList.length);
        const selectedJob = jobList[randomIndex];

        const cardWidth = 100;
        const newCard = {
            position: { x: pilePosition.x - cardWidth / 2, y: pilePosition.y - 150 },
            text: selectedJob.text,
            description: selectedJob.description,
        };

        const updatedJobList = jobList.filter((_, index) => index !== randomIndex);

        setGameData((prevGameData) => ({
            ...prevGameData,
            cardList: [...prevGameData.cardList, newCard],
            jobList: updatedJobList,
        }));
    };

    const handleAddCategory = () => {
        const { categoryList } = gameData;

        const newCategory = {
            position: { x: 10, y: 20 },
            text: "categorie " + (categoryList.length + 1),
        }

        setGameData((prevGameData) => ({
            ...prevGameData,
            categoryList: [...prevGameData.categoryList, newCategory],
        }));
    }

    const handleCardDragStart = (index) => {
        console.log(`Card picked! Card : ${gameData.cardList[index]}`);
        setGameData((prevGameData) => ({
            ...prevGameData,
            selectedCardIndex: index,
        }));
    };

    const isCollision = (card, dropZone) => {
        const cardRect = {
            left: card.position.x,
            right: card.position.x + 100, // Assuming card width is 100
            top: card.position.y,
            bottom: card.position.y + 70, // Assuming card height is 70
        };

        const dropZoneRect = {
            left: dropZone.position.x,
            right: dropZone.position.x + 100, // Assuming dropZone width is 100
            top: dropZone.position.y,
            bottom: dropZone.position.y + 70, // Assuming dropZone height is 70
        };

        // Check for collision
        return (
            cardRect.right > dropZoneRect.left &&
            cardRect.left < dropZoneRect.right &&
            cardRect.bottom > dropZoneRect.top &&
            cardRect.top < dropZoneRect.bottom
        );
    };

    const handleCardDrop = () => {
        const { selectedCardIndex, cardList, categoryList } = gameData;
        console.log(`Card dropped! Card : ${gameData.cardList[selectedCardIndex]}`);

        if (selectedCardIndex !== null) {
            const selectedCard = cardList[selectedCardIndex];

            // Check for collision with each drop zone
            categoryList.forEach((category) => {
                if (isCollision(selectedCard, category)) {
                    console.log(`Card dropped onto Category: ${category.text} ${selectedCard.text}`);
                }
            });

            // Check for collision with the discard zone
            if (isCollision(selectedCard, { position: discardPosition })) {
                console.log(`Card dropped onto Discard: ${selectedCard.text}`);
            }

            // Reset selectedCardIndex
            setGameData((prevGameData) => ({
                ...prevGameData,
                selectedCardIndex: null,
            }));
        }
    };

    return (
        <div>
            {!isCardPileDisabled && (
                <CardPile addCard={handleAddCard} counter={gameData.jobList.length} />
            )}
            <CardDiscard position={discardPosition}/>
            <button onClick={handleAddCategory}>créer une catégorie</button>
            {gameData.categoryList.map((category, index) => (
                <Category
                    key={index}
                    text={category.text}
                    position={category.position}
                />
            ))}
            {gameData.cardList.map((card, index) => (
                <Card
                    key={index}
                    text={card.text}
                    position={card.position}

                    onDragStart={() => handleCardDragStart(index)}
                    onDragEnd={handleCardDrop}
                />
            ))}
        </div>
    );
};

export default GamePage;
